INSERT INTO `lzh_navigation` VALUES ('52', '灵活宝', '/Agility/main/index.html', '', '', '', '7', '2', '2', '', '0', '1428656460', '0', 'navigation');
INSERT INTO `lzh_article_category` VALUES ('23', '灵活宝投标合同范本', '', '合同', '', '<h1 style=\"margin-left:0;text-indent:0;text-align:center\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 32px\">[web_name]</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 32px\">灵活宝投资协议</span></h1><p style=\"margin-left:0;text-indent:0;text-align:right;line-height:20px\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">灵活宝编号：</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">[batch_no]</span></p><p style=\"margin-left:0;text-indent:0;line-height:20px\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">甲方：</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">[company_name]</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">（提供互联网信息服务并拥有</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">[web_name]</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">网站</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">[domain]</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">）的经营权，简称平台）</span></p><p style=\"margin-left:0;text-indent:0;line-height:20px\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">住所：</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">[company_address]</span></p><p style=\"margin-left:0;text-indent:0;line-height:20px\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">乙方：</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">[invest_real_name]</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">（以下简称：乙方）</span></p><p style=\"margin-left:0;text-indent:0;line-height:20px\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">[web_name]</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">用户名：</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">[invest_user_name]</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">（以下简称：乙方）</span></p><p style=\"margin-left:0;text-indent:0;line-height:20px\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">身份证号码：</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">[invest_idcard]</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">（以下简称：乙方）</span></p><h2 style=\"margin-left:0;text-indent:0\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 18px\">投资信息：</span></h2><p style=\"margin-left:0;text-indent:0;line-height:20px\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">初始投资金额（人民币）：</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">[bao_item.money]</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">元</span></p><p style=\"margin-left:0;text-indent:0;line-height:20px\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">起息日期：</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">[bao_item.add_time]</span></p><p style=\"margin-left:0;text-indent:0;line-height:20px\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">计划封存期：</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">[bao_item.add_time]</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">起到</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">[bao_item.term_time_et]</span></p><p style=\"margin-left:0;text-indent:0;line-height:20px\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">最长投资期限：</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">[bao_item.add_time]</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">起到</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">[bao_item.time_et]</span></p><p style=\"margin-left:0;text-indent:0;line-height:20px\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">年化收益：</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">[bao_item.interest_rate]</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">%</span></p><p style=\"margin-left:0;text-indent:0;line-height:20px\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">收益分配方式：</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">收益复投</span></p><p style=\"margin-left:0;text-indent:0;line-height:20px\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">投资余额：根据乙方在</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">[web_name]</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">平台上投入本金、收益复投及本息赎回的情况由系统自动计算得出的余额为准。</span></p><p style=\"margin-left:0;text-indent:0;line-height:20px\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">甲、乙双方经友好协商，本着平等自愿、诚实信用的原则，就</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">[web_name]</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">提供的“灵活宝”相关事项订立有效合约，达成如下协议：</span></p><h3 style=\"margin-left:0;text-indent:0\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 18px\">一、主要内容</span></h3><p style=\"margin-left:0;text-indent:0;line-height:20px\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">1.1灵活宝：是由互联网平台</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">[web_name]</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">（以下简称平台）推出的，对经平台确认并由其合作的担保机构（包括但不限于小额贷款公司、融资性担保公司、大型电商平台等，以下简称：担保机构）承担全额本、息担保的借款项目进行自动分散投资的灵活方便的理财产品，投资、赎回、复投等均由投资人委托平台自动代为完成。该产品具有以下特点：一、计划封存期过后，可随时一次性或部分赎回本息（赎回后在投金额不得小于</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">[bao_item.start_funds]</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">元）；二、即投即算利息，每天还息，利息复投。</span></p><p style=\"margin-left:0;text-indent:0;line-height:20px\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">1.2&nbsp;借款资金来源保证：乙方保证其所用于出借的资金来源合法，乙方是该资金的合法所有人，如果第三方对资金归属、合法性问题发生争议，由乙方自行负责解决，如乙方未能解决，则放弃享有其所出借款项带来的利息收益。</span></p><h3 style=\"margin-left:0;text-indent:0\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 18px\">二、本协议的成立</span></h3><p style=\"margin-left:0;text-indent:0;line-height:20px\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">2.1&nbsp;本协议成立：本协议成立：乙方按照平台的规则，通过在平台“灵活宝”项目上点击确认“我同意”《</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">[web_name]</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">灵活宝投资协议》确认后，即视为乙方与平台已达成协议并同意接受本协议的全部约定内容以及与本协议有关的各项规则及</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">[web_name]</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">网站内所包含的其他与本协议或本协议项下各项规则的条款和条件有关的各项规定。</span></p><p style=\"margin-left:0;text-indent:0;line-height:20px\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">2.2投入资金冻结：投入资金冻结：乙方点击立即投资按钮后，即视为乙方已经向平台发出不可撤销的授权指令，授权平台在账户中冻结该笔资金。上述冻结资金在本协议生效时冻结或本协议确定失效时解除。</span></p><p style=\"margin-left:0;text-indent:0;line-height:20px\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">2.3&nbsp;在灵活宝期限内，除非本协议双方协商一致或本协议另有约定，本协议项下的期限、收益分配方式等一概不得变更。</span></p><h3 style=\"margin-left:0;text-indent:0\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 18px\">三、投资管理</span></h3><p style=\"margin-left:0;text-indent:0;line-height:20px\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">3.1&nbsp;乙方全权委托平台按照本协议的约定，将投资资金出借给平台上的经平台及平台合作机构推荐、并且得到平台合作机构担保的借款客户。</span></p><p style=\"margin-left:0;text-indent:0;line-height:20px\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">3.2&nbsp;投资项目：经平台确认的合作担保机构推荐、并且提供担保的借款项目。</span></p><p style=\"margin-left:0;text-indent:0;line-height:20px\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">3.3&nbsp;乙方投入灵活宝后，平台将按照乙方投入时间的先后顺序，将乙方投入灵活宝的资金进行投资。</span></p><p style=\"margin-left:0;text-indent:0;line-height:20px\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">3.4&nbsp;后续管理：乙方全权委托平台对本期灵活宝所投资项目的后续回款进行如下处理并代乙方签署相应的</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">[web_name]</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">借款协议及/或</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">[web_name]</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">债权转让协议：</span></p><p style=\"margin-left:0;text-indent:0;line-height:20px\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">3.4.1&nbsp;在本期灵活宝的存续投资期限内，若所投资项目借款企业提前还款，平台将把乙方所投资金余额自动投资到新的项目中，直至本期灵活宝期满。</span></p><p style=\"margin-left:0;text-indent:0;line-height:20px\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">3.4.2&nbsp;在本期灵活宝的存续投资期限内，乙方可以随时申请提前赎回投资。</span></p><p style=\"margin-left:0;text-indent:0;line-height:20px\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">3.5&nbsp;在本期灵活宝到期后，平台将借款客户还回的本息通过平台向乙方偿还投资本金以及收益。</span></p><h3 style=\"margin-left:0;text-indent:0\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 18px\">四、收益及费用</span></h3><p style=\"margin-left:0;text-indent:0;line-height:20px\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">4.1&nbsp;收益来源：乙方将自有资金出借给平台上的借款客户后，借款客户的利息还款将作为灵活宝的收益返还给乙方。</span></p><p style=\"margin-left:0;text-indent:0;line-height:20px\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">4.2&nbsp;收益起始时间：投资日期+1天，开始计算收益。</span></p><p style=\"margin-left:0;text-indent:0;line-height:20px\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">4.3收益分配方式：灵活宝收益复投。乙方在投入灵活宝产品时，可自主选择；一旦确认，在灵活宝产品期限内无法修改。</span></p><p style=\"margin-left:0;text-indent:0;line-height:20px\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">4.4收益返还：每天产生的利息会返回到本金里进行复投。</span></p><h3 style=\"margin-left:0;text-indent:0\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 18px\">五、赎回方式</span></h3><p style=\"margin-left:0;text-indent:0;line-height:20px\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">5.1本金提前赎回：乙方可在首次投资起息满7天后选择提前赎回其通过平台投入灵活宝的单笔全部或部分资金，未满7天不能发起赎回申请。乙方赎回的资金一律由所投融资项目的担保机构进行回购，自乙方赎回之日起原乙方的对应债权及后期收益自动转让至该担保机构。</span></p><p style=\"margin-left:0;text-indent:0;line-height:20px\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">5.2利息结算：本金提前赎回所产生的相应利息结算，截止到平台确认乙方的提前赎回申请的前一日。</span></p><h3 style=\"margin-left:0;text-indent:0\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 18px\">六、其他</span></h3><p style=\"margin-left:0;text-indent:0;line-height:20px\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">6.1&nbsp;本协议的任何修改、补充均须以</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">[web_name]</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">平台电子文本形式输出。</span></p><p style=\"margin-left:0;text-indent:0;line-height:20px\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">6.2&nbsp;各方均确认，本协议的签订、生效和履行以不违反法律为前提。如果本协议中的任何一条或多条违反适用的法律，则该条将被视为无效，但该无效条款并不影响本协议其他条款的效力。</span></p><p style=\"margin-left:0;text-indent:0;line-height:20px\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">6.3&nbsp;如双方在本协议履行过程中发生任何争议，应友好协商解决；如协商不成，则须提交甲方所在地人民法院进行诉讼。</span></p><p style=\"margin-left:0;text-indent:0;line-height:20px\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">&nbsp;</span></p><p style=\"margin-left:0;text-indent:0;line-height:20px\"><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">&nbsp;</span></p><p style=\"margin-left:0;text-indent:0;line-height:20px\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">甲方：</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">[company_name]</span></p><p style=\"margin-left:0;text-indent:0;line-height:20px\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">乙方：</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">[invest_real_name]</span></p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">合同签署日期：</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">[bao_item.add_time]</span></p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;[jujianfang.hetong_img]</p><p><br/></p>', '0', '0', '0', 'lhbht', '0', '1429878983', '0', 'article', null);
-- ----------------------------
-- Table structure for lzh_bao
-- ----------------------------
DROP TABLE IF EXISTS `lzh_bao`;
CREATE TABLE `lzh_bao` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `batch_no` varchar(50) NOT NULL COMMENT '项目编号',
  `name` varchar(30) NOT NULL COMMENT '项目名称',
  `funds` decimal(15,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '计划金额',
  `status` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '状态 0 未开启；1 开启（投资中） 2投标完成（还款中） 4 已还完结束',
  `raise_funds` decimal(15,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '已集资',
  `term` SMALLINT(5) unsigned NOT NULL DEFAULT '7' COMMENT '封存天数',
  `interest_rate` decimal(4,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '年利率',
  `add_ip` char(20) NOT NULL COMMENT '添加时Ip',
  `add_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '添加时间',
  `add_userid` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '创建人uid',
  `repayment_period` tinyint(6) unsigned NOT NULL DEFAULT '0' COMMENT '最大存储期限(月）',
  `start_funds` SMALLINT(6) unsigned NOT NULL DEFAULT '50' COMMENT '起投金额',
  `online_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '上线时间',
  PRIMARY KEY (`id`),
  KEY `online_time` (`online_time`),
  KEY `batch_no` (`batch_no`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='灵投宝项目信息表';

-- ----------------------------
-- Table structure for lzh_bao_invest
-- ----------------------------
DROP TABLE IF EXISTS `lzh_bao_invest`;
CREATE TABLE `lzh_bao_invest` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `batch_no` varchar(30) NOT NULL COMMENT '项目编号',
  `money` decimal(15,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '投入资金',
  `out_money` decimal(15,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '转出资金',
  `interest` decimal(10,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '利息',
  `uid` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '投资人id',
  `add_ip` char(20) NOT NULL DEFAULT '0' COMMENT '创建ip',
  `add_time` int(10) unsigned NOT NULL COMMENT '添加时间',
  `record_time` int(10) NOT NULL COMMENT '更新时间（一般是利息获得时间）',
  `deadline` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '还款时间',
  PRIMARY KEY (`id`),
  KEY `batch_no` (`batch_no`),
  KEY `record_time` (`record_time`),
  KEY `uid` (`uid`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='灵投宝资金日志表';

-- ----------------------------
-- Table structure for lzh_bao_log
-- ----------------------------
DROP TABLE IF EXISTS `lzh_bao_log`;
CREATE TABLE `lzh_bao_log` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `batch_no` varchar(50) NOT NULL COMMENT '灵活宝编号',
  `money` decimal(15,2) NOT NULL COMMENT '影响资金',
  `status` tinyint(1) unsigned NOT NULL COMMENT ' 0处理中、1成功、2退回、3打款中',
  `type` tinyint(1) unsigned NOT NULL COMMENT '1转入，2转出',
  `uid` int(10) unsigned NOT NULL COMMENT '用户编号',
  `user_name` char(10) NOT NULL COMMENT '',
  `add_time` int(10) unsigned NOT NULL COMMENT '添加时间',
  `add_ip` char(18) NOT NULL COMMENT '添加ip',
  `auditors_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '管理员Id',
  `auditors_user` char(30) NOT NULL COMMENT '管理员用户名',
  `auditors_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '',
  `auditors_ip` char(18) NOT NULL COMMENT '操作管理员ip',
  `remark` text,
  `archive_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '封存时间',
  PRIMARY KEY (`id`),
  KEY `batch_no` (`batch_no`),
  KEY `uid` (`uid`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='灵投宝资金变动表';

-- ----------------------------
-- Table structure for lzh_bao_record
-- ----------------------------
DROP TABLE IF EXISTS `lzh_bao_record`;
CREATE TABLE `lzh_bao_record` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `uid` int(10) unsigned NOT NULL COMMENT '用户编号',
  `orders` varchar(50) NOT NULL COMMENT '',
  `money` decimal(6,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '收益金额',
  `e_time` int(10) unsigned NOT NULL DEFAULT '0' ,
  `add_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '添加时间',
  `add_ip` char(18) NOT NULL COMMENT '添加ip',
  `status` tinyint(1) unsigned NOT NULL COMMENT '0失败，1成功',
  `funds` decimal(15,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '本金',
  `batch_no` varchar(50) NOT NULL COMMENT '灵活宝编号',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='灵活宝利息记录表';
