INSERT INTO `lzh_article_category` VALUES ('22', '企业直投投标协议合同范本', '', '合同', '', '<p style=\"margin-left: 252px;text-indent: 28px\"><span style=\";font-family:宋体;font-weight:bold;font-size:19px\">借款协议</span></p><table><tbody><tr style=\"height:41px\" class=\"firstRow\"><td width=\"130\" valign=\"center\" style=\"padding: 1px;\"><p style=\";text-align:center\"><span style=\";font-family:宋体;font-size:16px\">借款协议号</span></p></td><td width=\"130\" valign=\"center\" style=\"padding: 1px;\"><p style=\";text-align:center\"><span style=\";font-family:宋体;font-size:16px\">用户名&nbsp;</span></p></td><td width=\"130\" valign=\"center\" style=\"padding: 1px;\"><p style=\";text-align:center\"><span style=\";font-family:宋体;font-size:16px\">借出金额</span></p></td><td width=\"139\" valign=\"center\" style=\"padding: 1px;\"><p style=\";text-align:center\"><span style=\";font-family:宋体;font-size:16px\">借款期限</span></p></td><td width=\"136\" valign=\"center\" style=\"padding: 1px;\"><p style=\";text-align:center\"><span style=\";font-family:宋体;font-size:16px\">应收本息</span></p></td></tr><tr style=\"height:41px\"><td width=\"130\" valign=\"center\" style=\"padding: 1px;\"><p style=\";text-align:center\"><span style=\";font-family:宋体;font-size:16px\">LZBHT-000</span><span style=\";font-family:宋体;font-size:16px\">[borrow_id]</span></p></td><td width=\"130\" valign=\"center\" style=\"padding: 1px;\"><p style=\"text-align:center\"><span style=\";font-family:宋体;font-size:16px\">[invest_user_name]</span></p></td><td width=\"130\" valign=\"center\" style=\"padding: 1px;\"><p style=\"text-align:center\"><span style=\";font-family:宋体;font-size:16px\">[invest_capital]</span></p></td><td width=\"157\" valign=\"center\" style=\"padding: 1px; word-break: break-all;\"><p style=\";text-align:center\"><span style=\";font-family:宋体;font-size:16px\"></span></p><p><span style=\";font-family:宋体;font-size:16px\">[invest_duration]</span></p><p style=\";text-align:center\"><span style=\";font-family:宋体;font-size:16px\">&nbsp;</span><br/></p></td><td width=\"118\" valign=\"center\" style=\"padding: 1px;\"><p style=\"text-align:center\"><span style=\";font-family:宋体;font-size:16px\">[capital_interest]</span></p></td></tr></tbody></table><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">甲方（出借人）：&nbsp;</span><span style=\"font-family: 宋体;\">[business_name]</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">&nbsp;</span><span style=\";font-family:&#39;Times New Roman&#39;;color:rgb(255,0,0);font-size:16px\">（注：因计算中存在四舍五入，最后一期应收本息与之前略有不同！）</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">乙方（借款人）：</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">真实姓名：</span><span style=\";font-family:宋体;text-underline:single;font-size:16px\">[invest_real_name]</span></p><p><span style=\";font-family:宋体;font-size:16px\">[web_name]</span><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">用户名：</span><span style=\";font-family:宋体;text-underline:single;font-size:16px\">[invest_user_name]</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">&nbsp;</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">丙方（保证人）：</span><span style=\";font-family:宋体;text-underline:single;font-size:16px\">[company_name]</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">运营地址：</span><span style=\";font-family:宋体;text-underline:single;font-size:16px\">[company_address]</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">丙方联系电话：</span><span style=\";font-family:宋体;text-underline:single;font-size:16px\">[company_tel]</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">鉴于：</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">1.<span style=\"font-family:宋体\">丙方是一家合法成立并有效存续的有限责任公司，拥有&nbsp;</span></span><span style=\";font-family:宋体;text-underline:single;font-size:16px\">[domain]</span><span style=\";font-family:&#39;Times New Roman&#39;;text-underline:single;font-size:16px\">&nbsp;</span><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">网站（以下简称<span style=\"font-family:Times New Roman\">“</span><span style=\"font-family:宋体\">该网站</span><span style=\"font-family:Times New Roman\">”</span><span style=\"font-family:宋体\">）的经营权，提供信用咨询，为交易提供信息服务；</span></span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">2.<span style=\"font-family:宋体\">乙方已在该网站注册，并承诺其提供给丙方的信息是完全真实的；</span></span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">3.<span style=\"font-family:宋体\">甲方承诺对本协议涉及的借款具有完全的支配能力，是其自有闲散资金，为其合法所得；并承诺其提供给丙方的信息是完全真实的；</span></span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">4.<span style=\"font-family:宋体\">乙方有借款需求，甲方亦同意借款，双方有意成为借贷关系；&nbsp;</span></span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">各方协商一致，签订如下协议，共同遵守，共同遵照履行：</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">&nbsp;</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">&nbsp;</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">&nbsp;</span></p><p><span style=\";font-family:宋体;font-weight:bold;font-size:16px\">第一条&nbsp;&nbsp;&nbsp;借款基本信息</span></p><p><span style=\";font-family:宋体;font-size:16px\"></span></p><table width=\"641\"><tbody><tr style=\"height:21px\" class=\"firstRow\"><td width=\"208\" valign=\"top\" style=\"padding: 1px;\"><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">借款总金额</span></p></td><td width=\"433\" valign=\"top\" style=\"padding: 1px;\"><p><span style=\";font-family:宋体;font-size:16px\">[invest_capital]</span></p></td></tr><tr style=\"height:21px\"><td width=\"208\" valign=\"top\" style=\"padding: 1px;\"><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">一次性偿还该会员本息数</span></p></td><td width=\"433\" valign=\"top\" style=\"padding: 1px;\"><p><span style=\";font-family:宋体;font-size:16px\">[capital_interest]</span></p></td></tr><tr style=\"height:21px\"><td width=\"208\" valign=\"top\" style=\"padding: 1px;\"><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">还款方式</span></p></td><td width=\"433\" valign=\"top\" style=\"padding: 1px;\"><p><span style=\";font-family:宋体;font-size:16px\">[repayment_type]</span><span style=\"font-family:&#39;Times New Roman&#39;;font-size:16px\">&nbsp;</span></p></td></tr><tr style=\"height:21px\"><td width=\"208\" valign=\"top\" style=\"padding: 1px;\"><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">开始计息日</span></p></td><td width=\"433\" valign=\"top\" style=\"padding: 1px;\"><p><span style=\";font-family:宋体;font-size:16px\">[second_verify_time]</span></p></td></tr><tr style=\"height:21px\"><td width=\"208\" valign=\"top\" style=\"padding: 1px;\"><p><span style=\";font-family:宋体;font-size:16px\">还款日</span></p></td><td width=\"433\" valign=\"top\" style=\"padding: 1px;\"><p><span style=\";font-family:宋体;font-size:16px\">[deadline]</span></p></td></tr></tbody></table><p><span style=\";font-family:宋体;font-size:16px\">&nbsp;</span><br/></p><p><span style=\";font-family:宋体;font-weight:bold;font-size:16px\">第二条&nbsp;&nbsp;&nbsp;各方权利和义务&nbsp;</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">&nbsp;<span style=\"font-family:宋体\">甲方的权利和义务</span></span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">甲方应按合同约定的借款日将足额的借款本金支付给乙方。</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">甲方享有其所得借款项所带来的利息收益。</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">如乙方违约，甲方有权要求丙方提供其已获得的乙方信息。</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">无须通知乙方，甲方可以根据自己的意愿进行本协议下其对乙方的债权的转让。</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">在甲方的债权转让后，乙方需对债权受让人继续履行本协议下其对甲方的还款义务，不得以未接到债权转让通知为由拒绝履行还款义务。</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">甲方应主动缴纳由利息所得带来的可能的税费。</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">如乙方还款不足约定本金、利息及违约金的，甲方同意各自按照其借出款项比例收取还款<span style=\"font-family:Times New Roman\">&nbsp;&nbsp;</span><span style=\"font-family:宋体\">。</span></span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">乙方权利和义务</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">乙方必须按期足额向甲方偿还本金和利息。</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">乙方必须按期&nbsp;足额向丙方支付借款管理费用。</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">乙方承诺所借款项不用于任何违法用途。</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">乙方应确保其提供的信息和资料的真实性，不得提供虚假信息或隐瞒重要事实。</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">乙方有权了解其在丙方的信息评审进度及结果。</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">乙方不得将本协议项下的任何权利义务转让给任何其他方。</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">丙方的权利和义务</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">甲乙双方同意丙方有权代甲方每期收取甲方出借款项所对应的乙方每期偿还的本息，代收后按照甲方的要求进行处置。</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">甲方同意向乙方出借相应款项时，已委托丙方在本协议生效时将该笔借款直接划付至乙方账户。</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">甲乙双方同意丙方有权代甲方在有必要时对乙方进行贷款的违约提醒及催收工作，包括但不限于电话通知、发律师函、对乙方提起诉讼等。甲方&nbsp;在此确认明确委托丙方为其进行以上工作，并授权丙方可以将此工作委托给其他方进行。乙方对前述委托的提醒、催收事项已明确知晓并应积极配合。</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">丙方有权按月向乙方收取双方约定的借款管理费，并在有必要时对乙方进行违约提醒及催收工作，包括但不限于电话通知、发律师函、将乙方提起诉讼等。丙方有权将此违约提醒及催收工作委托给其他方进行。</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">丙方应对甲方和乙方的信息及本协议内容保密；如任何一方违约，或因相关权利部门要求（包括但不限于法院、仲裁机构、金融监管机构等），丙方有权披露。</span></p><p><span style=\";font-family:宋体;font-weight:bold;font-size:16px\">&nbsp;第三条&nbsp;&nbsp;借款管理费及居间服务费</span><span style=\";font-family:宋体;font-size:16px\">&nbsp;</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">在本协议中，<span style=\"font-family:Times New Roman\">“</span><span style=\"font-family:宋体\">借款管理费</span><span style=\"font-family:Times New Roman\">”</span><span style=\"font-family:宋体\">和</span><span style=\"font-family:Times New Roman\">“</span><span style=\"font-family:宋体\">居间服务费</span><span style=\"font-family:Times New Roman\">”</span><span style=\"font-family:宋体\">是指因丙方为乙方提供信用咨询、评估、还款提醒、账户管理、还款特殊情况沟通等系列信用相关服务而由乙方支付给丙方的报酬。</span></span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">对于丙方向乙方提供的一系列信用服务，乙方同意在借款成功时向丙方支付本次借款本金的<span style=\"font-family:Times New Roman\">2%</span><span style=\"font-family:宋体\">作为居间服务费，此笔费用乙方委托丙方在借款&nbsp;成功时从借款本金中扣除缴纳。在还款期间，乙方每月向丙方支付借款本金的</span><span style=\"font-family:Times New Roman\">0.3%</span><span style=\"font-family:宋体\">，作为借款管理费，借款管理费的缴纳时间与乙方向甲方支付本息的还款日一&nbsp;致。</span></span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">如乙方和丙方一致调整借款管理费时，无需经过甲方同意。</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">&nbsp;<span style=\"font-family:宋体\">违约责任</span></span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">合同各方均应严格履行合同义务，非经各方协商一致或依照本协议约定，任何一方不得解除本协议。</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">任何一方违约，违约方应承担因违约使得其他各方产生的费用损失，包括但不限于调查、诉讼费、律师费等，应有违约方承担。如违约方为乙方&nbsp;的，甲方有权立即解除本协议，并要求乙方立即偿还未偿还的本金、利息、罚息、违约金。此时，乙方还应向丙方支付所有应付的借款管理费。如本协议提前解除&nbsp;时，乙方在网站的账户里有任何余款，丙方有权按照本协议第四条第<span style=\"font-family:Times New Roman\">3</span><span style=\"font-family:宋体\">项的清偿顺序将乙方的余款用于清偿，并要求乙方支付因此产生的相关费用。</span></span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">乙方的每期还款均应按照如下顺序清偿：</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">根据本协议产生的其他全部费用：</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">罚息；</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">逾期管理费；</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">拖欠的利息；</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">拖欠的本金；</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">拖欠丙方的借款管理费；</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">正常的利息；</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">正常的本金；</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">丙方的借款管理费；</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">乙方应严格履行还款义务，如乙方逾期还款，则应按照下述条款向甲方支付逾期罚息，自逾期开始之后，逾期本金的正常利息停止计算。</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">罚息总额<span style=\"font-family:Times New Roman\">=</span><span style=\"font-family:宋体\">逾期本息总额</span><span style=\"font-family:Times New Roman\">x</span><span style=\"font-family:宋体\">对应罚息利率</span><span style=\"font-family:Times New Roman\">x</span><span style=\"font-family:宋体\">逾期天数；</span></span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">逾期天数&nbsp;<span style=\"font-family:Times New Roman\">1—30</span><span style=\"font-family:宋体\">天</span><span style=\"font-family:Times New Roman\">&nbsp;&nbsp;31</span><span style=\"font-family:宋体\">天及以上</span></span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">罚息利率&nbsp;<span style=\"font-family:Times New Roman\">0.05%&nbsp;&nbsp;&nbsp;0.1%</span></span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">乙方应严格履行还款义务，如乙方逾期还款，则应按照下述条款向丙方支付逾期管理费；</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">逾期管理费总额<span style=\"font-family:Times New Roman\">=</span><span style=\"font-family:宋体\">逾期本息总额</span><span style=\"font-family:Times New Roman\">x</span><span style=\"font-family:宋体\">对应逾期管理费率</span><span style=\"font-family:Times New Roman\">x</span><span style=\"font-family:宋体\">逾期天数；</span></span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">逾期天数<span style=\"font-family:Times New Roman\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;1—30</span><span style=\"font-family:宋体\">天</span><span style=\"font-family:Times New Roman\">&nbsp;&nbsp;&nbsp;&nbsp;31</span><span style=\"font-family:宋体\">天及以上</span></span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">逾期管理费费率<span style=\"font-family:Times New Roman\">&nbsp;&nbsp;&nbsp;&nbsp;0.1%&nbsp;&nbsp;&nbsp;0.5%</span></span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">如果乙方逾期支付任何一期还款超过<span style=\"font-family:Times New Roman\">30</span><span style=\"font-family:宋体\">天，或乙方在逾期后出现逃避、拒绝沟通或拒绝承认欠款事实等恶意行为，本协议项下的全部借款本息及借款管理费提前到期，乙方应立即清偿本协议下尚未偿付的全部本金、利息、罚息、借款管理费及根据本协议产生的其他全部费用。</span></span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">如果乙方逾期支付任何一起还款超过<span style=\"font-family:Times New Roman\">30</span><span style=\"font-family:宋体\">天，或乙方在逾期后出现逃避、拒绝沟通或拒绝承认欠款事实等恶意行为，丙方有权将乙方的</span><span style=\"font-family:Times New Roman\">“</span><span style=\"font-family:宋体\">逾期记录</span><span style=\"font-family:Times New Roman\">”</span><span style=\"font-family:宋体\">记入公民征信系统，丙方不承担任何法律责任。</span></span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">如果乙方逾期支付任何一期还款超过<span style=\"font-family:Times New Roman\">30</span><span style=\"font-family:宋体\">天，或乙方在逾期后出现逃避、拒绝沟通或拒绝承认欠款事实等恶意行为，丙方有权将乙方违约失信的相关信息及乙方其他信息向媒体、用人单位、公安机关、检察机关、法律机关等披露，丙方不承担任何责任。</span></span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">在乙方还清全部本金、利息、借款管理费、罚息、逾期管理费之前，罚息及逾期管理费的计算不停止。</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">本借款协议中的所有甲方与乙方之间的借款均是相互独立的，一旦乙方逾期未归还借款本息，任何一个甲方有权单独向乙方追索或者提起诉讼。如乙方逾期支付借款管理费或提供虚假信息的，丙方亦可单独向乙方追索或者提起诉讼，并先期代偿乙方所欠甲方的本金及利息。</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">提前还款</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">乙方可在借款期间任何时候提前偿还剩余借款。</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">提前偿还全部剩余借款</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">乙方提前清偿全部剩余及借款时，应向甲方支付当期应还本息，剩余本金及提前还款补偿（补偿金额为剩余本金的<span style=\"font-family:Times New Roman\">1%</span><span style=\"font-family:宋体\">）。</span></span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">乙方提前清偿全部剩余借款时，应向丙方支付当期借款管理费，乙方无需支付剩余还款期的借款管理费。</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">提前偿还部分借款</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">乙方提前偿还部分借款，仍应向甲方支付全部借款利息。</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">乙方提前偿还部分借款，仍应向丙方支付全部应付的借款管理费。</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">任何形式的提前还款不影响丙方向乙方收取在本协议第三条中说明的居间服务费。</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">&nbsp;</span></p><p><span style=\";font-family:宋体;font-weight:bold;font-size:16px\">&nbsp;第四条&nbsp;&nbsp;法律及争议解决&nbsp;</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">本协议的签订、履行、终止、解释均适用于中华人民共和国法律。</span></p><p><span style=\";font-family:宋体;font-weight:bold;font-size:16px\">&nbsp;附则</span><span style=\";font-family:宋体;font-size:16px\">&nbsp;</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">本协议采用电子文本形式制成，并永久保存在丙方为此设立的专用服务器上备查，各方均认可该形式的协议效力。</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">本协议自文本最终生成日生效。</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">本协议签订之日起至借款全部清偿之日止，乙方或甲方有义务在下列信息变更三日内提供更新后的信息给丙方：本人、本人的家庭联系人及紧急&nbsp;联系人、工作单位、居住地址、住所电话、手机号码、电子邮箱、银行账户的变更。若因任何一方不及时提供上述变更信息而带来的损失或额外费用应由该方承担。</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">如果本协议中的任何一条或多条违反适用的法律法规，则该条将被视为无效，但该无效条款并不影响本协议其他条款的效力。</span></p><p><span style=\";font-family:&#39;Times New Roman&#39;;font-size:16px\">&nbsp;</span></p><p><span style=\";font-family:宋体;font-size:16px\">居间方：&nbsp;</span><span style=\"font-family: 宋体;color: rgb(0, 0, 0);letter-spacing: 0;font-size: 14px\">[hetong_img]</span><span style=\";font-family:宋体;font-size:16px\"><br/></span><span style=\";font-family:宋体;font-size:16px\"></span></p><p><span style=\";font-family:宋体;font-size:14px\">[second_verify_time]</span></p><p><br/></p>', '0', '0', '0', 'ztht', '0', '1429878950', '0', 'article', null);
